import React, { useRef } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faGithub,
  faInstagram,
  faLinkedin,
} from "@fortawesome/free-brands-svg-icons";
import "./contact.css";
import emailjs from "emailjs.com";

const Contact = () => {
  const form = useRef(null);

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs
      .sendForm(
        "service_cacjbbd",
        "template_ypv3gue",
        form.current,
        "flZu90J56LAYfn2I8"
      )
      .then(
        () => console.log("Email sent successfully"),
        (error) => console.log(error)
      );
  };

  return (
    <section id="contactPage" className="container">
      <h1 className="contactTitle">Contact Me</h1>
      <p className="contactDesc">
        Want to get in touch? Fill out the form below to send me a message.
      </p>
      <form ref={form} className="contactForm" onSubmit={sendEmail}>
        <input
          type="text"
          name="your_name"
          placeholder="Your Name"
          className="form-control mb-3"
        />
        <input
          type="email"
          name="your_email"
          placeholder="Your Email"
          className="form-control mb-3"
        />
        <textarea
          name="message"
          rows="5"
          placeholder="Your Message"
          className="form-control mb-3"
        ></textarea>
        <button type="submit" className="btn btn-primary">
          Submit
        </button>
      </form>
      <div className="links mt-4">
        <a href="https://github.com/linhklatt" className="link mx-2">
          <FontAwesomeIcon icon={faGithub} />
        </a>
        <a href="https://linkedin.com/in/linh-klatt" className="link mx-2">
          <FontAwesomeIcon icon={faLinkedin} />
        </a>
        <a href="https://instagram.com/linh_klatt" className="link mx-2">
          <FontAwesomeIcon icon={faInstagram} />
        </a>
      </div>
    </section>
  );
};

export default Contact;
